const initialState = {
    pokemons: [],
    allPokemons: [],                                   //estado que siempre tiene todos pokemons
    types: [],
    detail: []
};

function rootReducer(state = initialState, action) {
    switch(action.type) {
        case "GET_POKEMONS":
            return{
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload                 //Estado para traer info original siempre.
            }
        case "FILTER_CREATED":
            const fullPokemons = state.allPokemons
            const createdFilter = action.payload === 'created' ? fullPokemons.filter(el => el.createdInDb) : fullPokemons.filter(el => !el.createdInDb)   
            return {
                ...state,
                pokemons: action.payload === 'All' ? state.allPokemons : createdFilter                
            }
        case "FILTER_TYPE":
            const fullPokemons2 = state.allPokemons
            //const result = fullPokemons2.filter(p => p.type == action.payload)
            const result = []
            for(let i = 0; i < fullPokemons2.length; i++){
                if(fullPokemons2[i].type.includes(action.payload)){
                    result.push([i])
                }
            }
            return {
                ...state,
                pokemons: result
            }

        case "ORDER_BY_NAME":
            let sortedArr = action.payload === 'asc' ?
                state.pokemons.sort(function(a, b){
                    if(a.name > b.name) {
                        return 1;
                    }
                    if(b.name > a.name) {
                        return -1;
                    }
                    return 0;
                }) :
                state.pokemons.sort(function(a, b){
                    if(a.name > b.name) {
                        return -1;
                    }
                    if(b.name > a.name) {
                        return 1;
                    }
                    return 0;
                })
                return {
                    ...state,
                    pokemons: sortedArr
                }
        case "GET_TYPES":
            return{
                ...state,
                types: action.payload
            }
        case "POST_POKEMON":
            return{
                ...state,
            }
        case "GET_DETAILS":
            return{
                ...state,
                detail: action.payload
            }    
        case "GET_NAME_POKEMONS":
                return{
                    ...state,
                    pokemons: action.payload
                }

        default:
            return state;
    }
}

export default rootReducer;
