import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPokemons } from '../actions';
import { Link } from 'react-router-dom';
import Card from './Card';
import rootReducer from '../reducer';

export default function Home(){
    const dispatch = useDispatch();
    const allPokemons = useSelector ((state) => state.pokemons)

    useEffect (() =>{                       //Manejo de ciclos de vida
        dispatch(getPokemons());
    }, [dispatch]);

    function handleClick(e){            //Reset
        e.preventDefault();             //preventDefault para que cuando recargue no se rompa todo.
        dispatch(getPokemons())
    };

    return (                                            //Renderizado del Componente
        <div>
            <Link to='/pokemon'>Crear Pok√©mon</Link>
            <h1>NOMBRE DE LA PAGINA POKEMON</h1>
            <button onClick={ e => {handleClick(e)}}>
                Volver a Cargar Todos Los Pokemons
            </button>
            <div>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='Desc'>Descendente</option>
                </select>
                <select>
                    <option value='All'>Todos</option>
                    <option value='created'>Creados</option>
                    <option value='api'>Existentes</option>
                </select>
                { allPokemons?.map((c) => {
                        return (
                            <fragment>
                                <Link to={"/home/" + c.id}>
                                <Card name={c.name} image={c.img} types={c.type} key={c.id}/>
                                </Link>
                            </fragment>
                        );
                    })
                }
            </div>
        </div>
    )
}
