import React, {useState, useEffect} from "react";
import {Link, useHistory} from 'react-router-dom';
import {postPokemon, getTypes} from '../actions/index';
import {useDispatch, useSelector} from "react-redux";

export default function PokemonCreate(){
    const dispatch = useDispatch();
    const types = useSelector((state) => state.types);

    const [input, setInput] = useState({
        name:"",
        health: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0,
        weight: 0,
        types: [],
    })

    function handleChange(e){
        setInput({
            ...input,
            [e.target.name] : e.target.value,
        })
    }
    
    useEffect(() => {
        dispatch(getTypes());
    }, [dispatch]);
    
    return(
        <div>
            <Link to='/home'><button>Volver</button></Link>
            <h1>Creá Tu Pokémon</h1>
            <form>
                <div>
                    <label>Nombre: </label>
                    <input type='text' value={input.name} name='name'/>
                </div>
                <div>
                    <label>Imagen: </label>
                    <input type='text' value={input.image} name='image'/>
                </div>
                <div>
                    <label>Vida: </label>
                    <input type='number' value={input.health} name='health'/>
                </div>
                <div>
                    <label>Ataque: </label>
                    <input type='number' value={input.attack} name='attack'/>
                </div>
                <div>
                    <label>Defensa: </label>
                    <input type='number' value={input.defense} name='defense'/>
                </div>
                <div>
                    <label>Velocidad: </label>
                    <input type='number' value={input.speed} name='speed'/>
                </div>
                <div>
                    <label>Tamaño: </label>
                    <input type='number' value={input.height} name='height'/>
                </div>
                <div>
                    <label>Peso: </label>
                    <input type='number' value={input.weight} name='weight'/>
                </div>
                <select>
                    {types.map((t) => (
                        <option value={t.name}>{t.name}</option>
                    ))}
                </select>

                <button type='submit'>Crear Pokémon</button>
                
            </form>
        </div>
    )
}
